package problem3297

import (
	"fmt"
	"reflect"
	"testing"
)

type TestCase struct {
	word1  string
	word2  string
	output int64
}

func TestValidSubstringCount(t *testing.T) {

	testCases := []TestCase{
		{
			word1:  "abcabc",
			word2:  "aaabc",
			output: 0,
		}, {
			word1: "bcca",
			word2: "abc",
			output: 1,
		},{
			word1:  "abcabc",
			word2:  "abc",
			output: 10,
		}, {
			word1:  "",
			word2:  "",
			output: 28053744,
		}, 
	}

	for i, testCase := range testCases {
		t.Run(fmt.Sprintf("valid_substring_count case: %d", i+1), func(t *testing.T) {
			res := ValidSubstringCount2(testCase.word1, testCase.word2)
			if !reflect.DeepEqual(res, testCase.output) {
				t.Errorf("Expected %v, got %v", testCase.output, res)
			}
		})
	}
}
